package numeros;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 0;
		
		int num1 = 101;
		
		while (num < 101) {
			
			System.out.println(num);
			
			num = num + 1;
		}
		
		
		for (int i = 0; i < num1; i++) {
			
			System.out.println(i);
			
			
		}
		

	}

}
